+     VALUES ($1, $2, $3, $4, $5)`,
+    [stockId, quote.price, quote.currency, new Date(quote.timestamp), quote.volume]
+  );
+
+  // Get previous quote for price change calculation
+  const prevQuote = await query(
+    `SELECT price 
+     FROM quotes 
+     WHERE stock_id = $1 
+     AND timestamp < $2 
