{"tool":"newFileCreated","path":"bff/src/controllers/categoryController.ts","content":"import { Request, Response } from 'express';
import { CategoryService } from '../services/categoryService';
import { validateUUID } from '../utils/validation';

export class CategoryController {
    constructor(private categoryService: CategoryService) {}

    createCategory = async (req: Request, res: Response): Promise<void> => {
        try {
            const category = await this.categoryService.createCategory(req.body);
            res.status(201).json(category);
        } catch (error) {
            if (error instanceof Error && error.message === 'Category with this name already exists') {
                res.status(409).json({ error: error.message });
            } else {
                res.status(500).json({ error: 'Failed to create category' });
            }
        }
    };

    getCategoryById = async (req: Request, res: Response): Promise<void> => {
        try {
            const { id } = req.params;
            if (!validateUUID(id)) {
                res.status(400).json({ error: 'Invalid category ID format' });
                return;
            }

            const category = await this.categoryService.getCategoryById(id);
            if (!category) {
                res.status(404).json({ error: 'Category not found' });
                return;
            }
            res.json(category);
        } catch (error) {
            res.status(500).json({ error: 'Failed to retrieve category' });
        }
    };

    getAllCategories = async (_req: Request, res: Response): Promise<void> => {
        try {
            const categories = await this.categoryService.getAllCategories();
            res.json(categories);
        } catch (error) {
            res.status(500).json({ error: 'Failed to retrieve categories' });
        }
    };

    updateCategory = async (req: Request, res: Response): Promise<void> => {
        try {
            const { id } = req.params;
            if (!validateUUID(id)) {
                res.status(400).json({ error: 'Invalid category ID format' });
                return;
            }

            const category = await this.categoryService.updateCategory(id, req.body);
            res.json(category);
        } catch (error) {
            if (error instanceof Error) {
                if (error.message === 'Category not found') {
                    res.status(404).json({ error: error.message });
                } else if (error.message === 'Category with this name already exists') {
                    res.status(409).json({ error: error.message });
                } else {
                    res.status(500).json({ error: 'Failed to update category' });
                }
            }
        }
    };

    deleteCategory = async (req: Request, res: Response): Promise<void> => {
        try {
            const { id } = req.params;
            if (!validateUUID(id)) {
                res.status(400).json({ error: 'Invalid category ID format' });
                return;
            }

            await this.categoryService.deleteCategory(id);
            res.status(204).send();
        } catch (error) {
            if (error instanceof Error && error.message === 'Category not found') {
                res.status(404).json({ error: error.message });
            } else {
                res.status(500).json({ error: 'Failed to delete category' });
            }
        }
    };
}"}

