  }

  const totalGainLoss = totalValue - totalCost;
  const totalGainLossPercentage = totalCost > 0 ? (totalGainLoss / totalCost) * 100 : 0;

  return {
    id: portfolio.PORTFOLIOS_ID,
    name: portfolio.NAME,
    totalValue,
    totalGainLoss,
    totalGainLossPercentage,
    holdingsCount: holdings.length
  };
