  priceChange?: number;
  priceChangePercentage?: number;
  marketCap?: number;
  volume?: number;
  peRatio?: number;
  dividendYield?: number;
  yearHigh?: number;
  yearLow?: number;
}

import { Router } from 'express';
import { protect } from '../config/keycloak';
import * as transactionController from '../controllers/transactionController';
import { validateTransactionCreation, validateUUID } from '../middleware/validation';

const router = Router();

// All transaction routes require authentication
router.use(protect());

// Create transaction for a holding
router.post('/holding/:holdingId', 
    validateUUID('holdingId'),
    validateTransactionCreation,
    transactionController.createTransaction
);

// Get transactions by holding with optional filters
// Query params:
// - startDate: ISO date string
// - endDate: ISO date string
// - type: 'BUY' | 'SELL'
// - sort: 'date' | 'amount' | 'price'
// - order: 'asc' | 'desc'
// - page: number
// - limit: number
router.get('/holding/:holdingId',
    validateUUID('holdingId'),
    transactionController.getTransactionsByHolding
);

// Get transactions by portfolio with optional filters
// Query params:
// - startDate: ISO date string
// - endDate: ISO date string
// - type: 'BUY' | 'SELL'
// - sort: 'date' | 'amount' | 'price'
// - order: 'asc' | 'desc'
// - page: number
// - limit: number
router.get('/portfolio/:portfolioId',
    validateUUID('portfolioId'),
    transactionController.getTransactionsByPortfolio
);

// Get single transaction by ID
router.get('/:id',
    validateUUID('id'),
    transactionController.getTransaction
);

export default router;

// Base interface matching DB model
export interface Transaction {
    TRANSACTIONS_ID: string;
    HOLDINGS_ID: string;
    BUY: boolean;
    TRANSACTION_TIME: Date;
    AMOUNT: number;
    PRICE: number;
    COMMISSION: number;
    BROKER: string;
}

// DTO for API requests
export interface CreateTransactionDTO {
    AMOUNT: number;
    PRICE: number;
    BUY: boolean;
    COMMISSION?: number;
    BROKER?: string;
}

// Query parameters for transaction filtering
export interface TransactionQueryParams {
    startDate?: string;
    endDate?: string;
    type?: 'BUY' | 'SELL';
    sort?: 'date' | 'amount' | 'price';
    order?: 'asc' | 'desc';
    page?: number;
    limit?: number;
}

// Paginated response interface
export interface PaginatedTransactions {
    transactions: Transaction[];
    total: number;
    page: number;
    limit: number;
    totalPages: number;
}

