  req: Request<{ id: string }, {}, UpdateUserDTO>,
  res: Response,
  next: NextFunction
) => {
  try {
    const userId = req.params.id;
    const updateData = req.body;
    const updatedUser = await userService.updateUser(userId, updateData);
    if (!updatedUser) {
      return res.status(404).json({ message: 'User not found' });
