    ISIN: 'US0378331005',
    SYMBOL: 'AAPL',
    NAME: 'Apple Inc.',
    WKN: '123456',
    CATEGORIES_ID: 'tech-category'
  };

  const mockYahooQuote: YahooFinanceQuote = {
    price: 150.50,
    currency: 'USD',
    exchange: 'NASDAQ',
    timestamp: 1625097600000,
    volume: 1000000,
    open: 149.50,
    high: 152.00,
    low: 148.50,
