    res.json({ performance });
  } catch (error) {
    if (error instanceof Error) {
      if (error.message === 'Holding not found') {
        res.status(404).json({ error: error.message });
      } else {
        next(error);
      }
    } else {
      next(error);
    }
  }
};

export const getHoldingTransactions = async (
  req: AuthenticatedRequest,
  res: Response,
