          "                    { key: 'username', value: username },",
          "                    { key: 'password', value: password }",
          "                ]",
          "            }",
          "        }, function (err, res) {",
          "            if (err) {",
          "                console.error(err);",
          "            } else {",
          "                const response = res.json();",
