import type { TypedRequest, TypedResponse, NextFunction, AuthenticatedRequest } from '../types/express';
import { User, CreateUserDTO, UpdateUserDTO } from '../models/User';
import { userService } from '../services/userService';

// Define response types
type UserResponse = { user: User };
type ErrorResponse = { error: string };

export const createUser = async (
  req: TypedRequest<{}, {}, CreateUserDTO>,
  res: TypedResponse<UserResponse | ErrorResponse>,
  next: NextFunction
) => {
  try {
    const userData = req.body;
    const user = await userService.createUser(userData);
    res.status(201).json({ user });
  } catch (error) {
    if (error instanceof Error && error.message.includes('already exists')) {
      res.status(409).json({ error: error.message });
    } else {
      next(error);
    }
  }
};

export const getUser = async (
  req: TypedRequest<{ id: string }>,
  res: TypedResponse<UserResponse | ErrorResponse>,
  next: NextFunction
) => {
  try {
    const userId = req.params.id;
    const user = await userService.getUserById(userId);
    if (!user) {
      return res.status(404).json({ error: 'User not found' });
    }
    res.json({ user });
  } catch (error) {
    next(error);
  }
};

export const updateUser = async (
  req: TypedRequest<{ id: string }, {}, UpdateUserDTO>,
  res: TypedResponse<UserResponse | ErrorResponse>,
  next: NextFunction
) => {
  try {
    const userId = req.params.id;
    const updateData = req.body;
    const updatedUser = await userService.updateUser(userId, updateData);
    if (!updatedUser) {
      return res.status(404).json({ error: 'User not found' });
    }
    res.json({ user: updatedUser });
  } catch (error) {
    next(error);
  }
};

export const deleteUser = async (
  req: TypedRequest<{ id: string }>,
  res: TypedResponse<void | ErrorResponse>,
  next: NextFunction
) => {
  try {
    const userId = req.params.id;
    await userService.deleteUser(userId);
    res.status(204).send();
  } catch (error) {
    if (error instanceof Error && error.message === 'User not found') {
      res.status(404).json({ error: error.message });
    } else {
      next(error);
    }
  }
};

export const getOwnProfile = async (
  req: AuthenticatedRequest,
  res: TypedResponse<UserResponse | ErrorResponse>,
  next: NextFunction
) => {
  try {
    const userId = req.user.id;
    const user = await userService.getUserById(userId);
    if (!user) {
      return res.status(404).json({ error: 'User not found' });
    }
    res.json({ user });
  } catch (error) {
    next(error);
  }
};

export const updateOwnProfile = async (
  req: AuthenticatedRequest<{}, {}, UpdateUserDTO>,
  res: TypedResponse<UserResponse | ErrorResponse>,
  next: NextFunction
) => {
  try {
    const userId = req.user.id;
    const updateData = req.body;
    const updatedUser = await userService.updateUser(userId, updateData);
    if (!updatedUser) {
      return res.status(404).json({ error: 'User not found' });
    }
    res.json({ user: updatedUser });
  } catch (error) {
    next(error);
  }
};

